CREATE TABLE IF NOT EXISTS Item(ItemID INT(11) NOT NULL, ItemName VARCHAR(200) NOT NULL, ItemCurrentprice DECIMAL(8,2), ItemBuyprice DECIMAL(8,2), ItemFirstBid DECIMAL(8,2), ItemNumberOfBid INT(11), ItemLocation VARCHAR(200), LocationLatitude DOUBLE(10,6) DEFAULT NULL, LocationLongitude DOUBLE(10,6) DEFAULT NULL, Country VARCHAR(100), Started TIMESTAMP, Ends TIMESTAMP, SellerID VARCHAR(100) NOT NULL, Description VARCHAR(4000) NOT NULL, PRIMARY KEY(ItemID));
CREATE TABLE IF NOT EXISTS Category (ItemID INT(11) NOT NULL, Category VARCHAR(100) NOT NULL);
CREATE TABLE IF NOT EXISTS User(UserID VARCHAR(100) NOT NULL, SellerRating INT(11), BidderRating INT(11) DEFAULT NULL, Country VARCHAR(100) DEFAULT NULL, Location VARCHAR(100), PRIMARY KEY(UserID));
CREATE TABLE IF NOT EXISTS Bid (ItemID VARCHAR(100) NOT NULL, Time TIMESTAMP NOT NULL, BidderID VARCHAR(100) NOT NULL, Amount DECIMAL(8,2) NOT NULL,PRIMARY KEY(ItemID, Time, BidderID));
